import React from "react";

function FavoriteCards({meal, handleDelete}) {

function handleDelete(){
    fetch(`http://localhost:8000/meals/${meal.id}`, {
        method: "DELETE"
    })
    .then(()=>console.log("deleted"))

}

  return (
    <>
    <h1>{meal.strMeal}</h1>
    <img height="300" width="300" src={meal.strMealThumb} />
    <button  onClick={handleDelete}>I don't need you anymore as I have mastered the recipe</button>
    </>

  )
}

export default FavoriteCards;
