import React from "react";

function FavoriteCards({meal, handleDelete, handleVideoClick, showVideo}) {



  return (
    <>
    <h1>{meal.strMeal}</h1>
    {showVideo ? <ReactPlayer  url={meal.strYoutube}/> : <img onClick={handleVideoClick} height="300" width="300" src={meal.strMealThumb} alt={meal.strMeal}/>  }
    {/* <img height="300" width="300" src={meal.strMealThumb} /> */}
    <button  onClick={()=>handleDelete(meal)}>I don't need you anymore as I have mastered the recipe</button>
    </>

  )
}

export default FavoriteCards;
