import React, {useEffect, useState} from "react";
import { Route, Switch } from "react-router-dom";
import Home from "./Home";
import Search from "./Search";
import Favorites from "./Favorites";
import NavBar from "./NavBar";

function App() {
  const [randomRecipe, setRandomRecipe] = useState(false)

  useEffect(() => {
    fetch("https://www.themealdb.com/api/json/v1/1/random.php")
    .then((resp)=>resp.json())
    .then((recipe)=> setRandomRecipe(recipe))
  }, [])

  const trial = randomRecipe ? randomRecipe["meals"][0] : null
  console.log(trial)

  function handleAddToFavorite(){
    const newMeal = {
      "idMeal": trial.idMeal,
      "strMeal": trial.strMeal,
      "strCategory": trial.strCategory,
      "strInstructions": trial.strInstructions,
      "strMealThumb": trial.strMealThumb,
      "strYoutube": trial.strYoutube
  }
  console.log(newMeal)
    fetch("http://localhost:8000/meals", {
      method:"POST",
      headers:{
        "Content-Type": "application/json",
      },
      body:JSON.stringify(newMeal)
    })
    .then((resp)=>resp.json())
    .then((newMeal)=>console.log(newMeal))
  }



  return (
    <div>
      <NavBar />
      <Switch>
        <Route exact path="/search">
          <Search />
        </Route>
        <Route exact path="/favorites">
          <Favorites />
        </Route>
        <Route exact path="/">
          <Home trial={trial} handleAddToFavorite={handleAddToFavorite} randomRecipe={randomRecipe}/>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
