  //   // use keyName to get current key's name
  //   console.log(keyName)
  //   // and a[keyName] to get its value
  // })
console.log(individualRecipe)

  if (individualRecipe){
    return(
      <>
      <h1>SPICE IT UP!</h1>
      <h2>Helping You Put The Spice Into Your Meal Planning!</h2>
      <h2>{individualRecipe.strMeal}</h2> 
      {showVideo ? <ReactPlayer  url={individualRecipe.strYoutube}/> : <img onClick={handleVideoClick} height="300" width="300" src={individualRecipe.strMealThumb} alt={individualRecipe.strMeal}/>  }
      {showVideo ? null : <h3>Like what you see? Click the picture for a recipe video.</h3> }
      {showVideo ? <button onClick={handleVideoClick}>Hide Video</button>: null}
       {<button onClick={handleShowRecipe}>{showRecipe? "Hide Instructions!":"Show Instructions!"}</button>}
      {showRecipe ? <p>{individualRecipe.strInstructions}</p> : null}
      <h4 onClick={handleInstructions}>Click for Instructions!</h4> 

    {instructionDisplay ? <p>{meal.strInstructions}</p> : null}
    <h4 onClick={handleIngredients}>Click for Ingredients!</h4>
   {ingredientsDisplay ?
   <p>{meal.strMeasure1}-{meal.strIngredient1}, {meal.strMeasure2}-{meal.strIngredient2}, 
    {meal.strMeasure3}-{meal.strIngredient3}, {meal.strMeasure4}-{meal.strIngredient4}, 
    {meal.strMeasure5}-{meal.strIngredient5}, {meal.strMeasure6}-{meal.strIngredient6}, 
    {meal.strMeasure7}-{meal.strIngredient7}, {meal.strMeasure8}-{meal.strIngredient8}, 
    {meal.strMeasure9}-{meal.strIngredient9}, {meal.strMeasure10}-{meal.strIngredient10}, 
    {meal.strMeasure11}-{meal.strIngredient11}, </p>
    : null }
      <span>
        <button onClick={handleAddToFavorite}>Add to favorites</button>
      </span>
      <span>
        <button onClick={handleClickForNewPic}>Gimme another recipe</button>
      </span>
      </>
    )
  }else {
    return <h3>Loading...</h3>
  }

}

export default Home;
