//            "Content-type": "application/json"
//          },
//          body: JSON.stringify(newMeal)
//        })
//        .then(response => response.json())
//        .then(data => setAllRecipes([data, ...allRecipes]))
//        setAllRecipes({
//         "strMeal": "",
//         "strIngredients": "",
//         "strInstructions": "",
//         "strMealThumb": "",
//          })
//      }

     useEffect(() => {
        fetch("http://localhost:8000/recipes")
        .then(response => response.json())
        .then(data => setAllRecipes(data))
     }, [])



     function deleteRecipe(recipe){
      fetch(`http://localhost:8000/recipes/${recipe.id}`, {
        method: "DELETE"
      })
      .then(()=> setAllRecipes(allRecipes.filter((meal)=> meal.id !== recipe.id)))
  }


 return (
  <div className="new-recipe-form">
    <h2>New Recipe</h2>
    <form  onSubmit={handleSubmit}>
      <input   value={formState.strMeal} type="text" name="name" placeholder="Recipe name" />
      <input   value={formState.image} type="text" name="image" placeholder="Image URL" />
      <input   value={formState.name} type="text" name="ingredients" placeholder="Ingredients"/>
      <input   value={formState.name} type="text" name="instructions"  placeholder="Instructions" />
      <button  type="submit"> Add Recipe</button>
    </form>
  {allRecipes.map((recipe)=> <RecipeCard recipe={recipe} deleteRecipe={deleteRecipe}/>)}
  </div>
)
}
export default Add;
